package HomeAssignment.Week1;

public class CheckNumberIsPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =100;
		int b = -100;
		int c = 0;
		
		if(a>0)
		{
			System.out.println("The Given Number " +a + " is Positive");
		
		}
		
		else if(a<0)
		{
			
				System.out.println("The Given Number " +a + " is Negative");
			
			
		}
			
		else if (a==0)
			{
				
					System.out.println("The Given Number " +a + " is Zero");
				
			
			}
		
		if(b<0)
		{
			System.out.println("The Given Number " +b + " is Negative");
		
		}
		
		
		if(c==0)
		{
			System.out.println("The Given Number " +c + " is Zero");
		
		}
		
		
		
		}
		 
	}


